import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, decimal, boolean, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  firstName: text("first_name").notNull(),
  lastName: text("last_name").notNull(),
  username: text("username").notNull().unique(),
  phone: text("phone"),
  dateOfBirth: text("date_of_birth"),
  nationality: text("nationality"),
  profileImageUrl: text("profile_image_url"),
  createdAt: text("created_at").notNull().default(sql`CURRENT_TIMESTAMP`),
  updatedAt: text("updated_at").notNull().default(sql`CURRENT_TIMESTAMP`),
});

export const destinations = pgTable("destinations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  country: text("country").notNull(),
  district: text("district").notNull(),
  description: text("description").notNull(),
  imageUrl: text("image_url").notNull(),
  rating: text("rating").notNull(),
  priceFrom: integer("price_from").notNull(),
});

export const hotels = pgTable("hotels", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  location: text("location").notNull(),
  description: text("description").notNull(),
  imageUrl: text("image_url").notNull(),
  rating: text("rating").notNull(),
  pricePerNight: integer("price_per_night").notNull(),
  phone: text("phone").notNull(),
  amenities: text("amenities").array().notNull().default([]),
});

export const tripPlanners = pgTable("trip_planners", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  origin: text("origin").notNull(),
  destination: text("destination").notNull(),
  price: integer("price").notNull(),
  duration: text("duration").notNull().default(''),
  stops: text("stops").notNull().default(''),
  departureDate: text("departure_date").notNull().default(''),
  returnDate: text("return_date").notNull().default(''),
  dealType: text("deal_type").notNull().default(''),
});

export const buses = pgTable("buses", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  operator: text("operator").notNull(),
  type: text("type").notNull(),
  origin: text("origin").notNull(),
  destination: text("destination").notNull(),
  departure: text("departure").notNull(),
  arrival: text("arrival").notNull(),
  duration: text("duration").notNull(),
  price: integer("price").notNull(),
  seats: integer("seats").notNull(),
  amenities: text("amenities").array().notNull().default([]),
  rating: text("rating").notNull(),
});

export const privateCars = pgTable("private_cars", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  type: text("type").notNull(),
  category: text("category").notNull(),
  origin: text("origin").notNull(),
  destination: text("destination").notNull(),
  capacity: integer("capacity").notNull(),
  duration: text("duration").notNull(),
  price: integer("price").notNull(),
  driver: text("driver").notNull(),
  features: text("features").array().notNull().default([]),
  rating: text("rating").notNull(),
});

export const travelPackages = pgTable("travel_packages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  description: text("description").notNull(),
  imageUrl: text("image_url").notNull(),
  duration: text("duration").notNull(),
  rating: text("rating").notNull(),
  price: integer("price").notNull(),
  includes: text("includes").array().notNull().default([]),
});

export const restaurants = pgTable("restaurants", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  location: text("location").notNull(),
  description: text("description").notNull(),
  imageUrl: text("image_url").notNull(),
  rating: text("rating").notNull(),
  cuisine: text("cuisine").notNull(),
  priceRange: text("price_range").notNull(),
  phone: text("phone").notNull(),
  reviews: text("reviews").array().notNull().default([]),
});

export const bookings = pgTable("bookings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id"),
  itemId: varchar("item_id").notNull(),
  itemType: text("item_type").notNull(), // 'hotel', 'restaurant', 'flight', 'bus', 'car'
  customerName: text("customer_name").notNull(),
  email: text("email").notNull(),
  phone: text("phone").notNull(),
  passengers: integer("passengers"),
  specialRequests: text("special_requests"),
  travelDate: text("travel_date"),
  status: text("status").notNull().default('confirmed'), // 'confirmed', 'cancelled', 'pending'
  // Property information
  propertyName: text("property_name"),
  propertyLocation: text("property_location"),
  propertyImageUrl: text("property_image_url"),
  propertyPhone: text("property_phone"),
  confirmationNumber: text("confirmation_number"),
  bookingType: text("booking_type"), // 'hotel', 'restaurant'
  // Hotel specific fields
  roomType: text("room_type"),
  checkIn: text("check_in"),
  checkOut: text("check_out"),
  nights: integer("nights"),
  guests: integer("guests"),
  totalAmount: integer("total_amount"),
  // Restaurant specific fields
  reservationDate: text("reservation_date"),
  reservationTime: text("reservation_time"),
  partySize: integer("party_size"),
  cuisine: text("cuisine"),
  priceRange: text("price_range"),
  // Bus specific fields
  selectedSeats: text("selected_seats").array(),
  createdAt: timestamp("created_at").notNull().defaultNow(),
  updatedAt: timestamp("updated_at").notNull().defaultNow(),
});

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const updateUserSchema = createInsertSchema(users).omit({
  id: true,
  email: true,
  password: true,
  createdAt: true,
  updatedAt: true,
}).partial();

export const loginSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

export const insertDestinationSchema = createInsertSchema(destinations).omit({
  id: true,
});

export const insertHotelSchema = createInsertSchema(hotels).omit({
  id: true,
});

export const insertTripPlannerSchema = createInsertSchema(tripPlanners).omit({
  id: true,
});

export const insertBusSchema = createInsertSchema(buses).omit({
  id: true,
});

export const insertPrivateCarSchema = createInsertSchema(privateCars).omit({
  id: true,
});

export const insertTravelPackageSchema = createInsertSchema(travelPackages).omit({
  id: true,
});

export const insertRestaurantSchema = createInsertSchema(restaurants).omit({
  id: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type UpdateUser = z.infer<typeof updateUserSchema>;
export type LoginCredentials = z.infer<typeof loginSchema>;

export const insertBookingSchema = createInsertSchema(bookings).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertBooking = z.infer<typeof insertBookingSchema>;
export type Booking = typeof bookings.$inferSelect;
export type InsertDestination = z.infer<typeof insertDestinationSchema>;
export type InsertHotel = z.infer<typeof insertHotelSchema>;
export type InsertTripPlanner = z.infer<typeof insertTripPlannerSchema>;
export type InsertBus = z.infer<typeof insertBusSchema>;
export type InsertPrivateCar = z.infer<typeof insertPrivateCarSchema>;
export type InsertTravelPackage = z.infer<typeof insertTravelPackageSchema>;
export type InsertRestaurant = z.infer<typeof insertRestaurantSchema>;
export type User = typeof users.$inferSelect;
export type Destination = typeof destinations.$inferSelect;
export type Hotel = typeof hotels.$inferSelect;
export type TripPlanner = typeof tripPlanners.$inferSelect;
export type Bus = typeof buses.$inferSelect;
export type PrivateCar = typeof privateCars.$inferSelect;
export type TravelPackage = typeof travelPackages.$inferSelect;
export type Restaurant = typeof restaurants.$inferSelect;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import Home from "@/pages/home";
import About from "@/pages/about";
import Destinations from "@/pages/destinations";
import DestinationDetail from "@/pages/destination-detail";
import Hotels from "@/pages/hotels";
import TripPlanner from "@/pages/trip-planner";
import MyBookings from "@/pages/my-bookings";
import HotelBooking from "@/pages/hotel-booking";
import RestaurantBooking from "@/pages/restaurant-booking";
import TransportBooking from "@/pages/transport-booking";
import Support from "@/pages/support";
import SignIn from "@/pages/sign-in";
import SignUp from "@/pages/sign-up";
import Profile from "@/pages/profile";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route path="/destinations" component={Destinations} />
      <Route path="/destinations/:id" component={DestinationDetail} />
      <Route path="/hotels" component={Hotels} />
      <Route path="/trip-planner" component={TripPlanner} />

      <Route path="/my-bookings" component={MyBookings} />
      <Route path="/hotel-booking/:id" component={HotelBooking} />
      <Route path="/restaurant-booking/:id" component={RestaurantBooking} />
      <Route path="/transport-booking/:type/:id" component={TransportBooking} />
      <Route path="/about" component={About} />
      <Route path="/support" component={Support} />
      <Route path="/sign-in" component={SignIn} />
      <Route path="/sign-up" component={SignUp} />
      <Route path="/profile" component={Profile} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

