import { Heart, ArrowUpDown, ChevronLeft, ChevronRight } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useQuery } from '@tanstack/react-query';
import type { TripPlanner } from '@shared/schema';

export default function TripPlannerDeals() {
  const { data: tripPlanners = [], isLoading, error } = useQuery<TripPlanner[]>({
    queryKey: ['/api/trip-planners'],
  });

  const handleBookNow = () => {
    // Navigate to trip planner page for booking
    window.location.href = '/trip-planner';
  };

  // Map trip planners to destination images
  const getDestinationImage = (destination: string) => {
    const imageMap: { [key: string]: string } = {
      "Cox's Bazar": "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300",
      "Sylhet": "https://images.unsplash.com/photo-1544551763-46a013bb70d5?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300",
      "Chittagong": "https://images.unsplash.com/photo-1571896349842-33c89424de2d?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300",
      "Jessore": "https://images.unsplash.com/photo-1566073771259-6a8506099945?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300"
    };
    return imageMap[destination] || "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300";
  };

  if (isLoading) {
    return (
      <section id="trip-planners" className="py-16 bg-white">
        <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">Trip Planner Deals</h2>
            <p className="text-gray-600">Plan your perfect journey with our curated travel packages.</p>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-4xl mx-auto">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-white rounded-xl shadow-md animate-pulse">
                <div className="w-full h-48 bg-gray-200 rounded-t-xl"></div>
                <div className="p-4">
                  <div className="h-6 bg-gray-200 rounded mb-2"></div>
                  <div className="h-4 bg-gray-200 rounded mb-4"></div>
                  <div className="flex justify-between items-center">
                    <div className="h-8 bg-gray-200 rounded w-24"></div>
                    <div className="h-10 bg-gray-200 rounded w-20"></div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section id="trip-planners" className="py-16 bg-white">
        <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">Trip Planner Deals</h2>
            <p className="text-red-600">Failed to load trip planner deals. Please try again later.</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="trip-planners" className="py-8 sm:py-12 lg:py-16 section-bg-warm">
      <div className="max-w-6xl mx-auto px-3 sm:px-4 lg:px-8">
        <div className="text-center mb-8 sm:mb-10 lg:mb-12">
          <div className="flex justify-center mb-3 sm:mb-4">
            <div className="icon-bg-orange">
              <svg className="w-5 sm:w-6 h-5 sm:h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 20l-5.447-2.724A1 1 0 013 16.382V5.618a1 1 0 011.447-.894L9 7m0 13l6-3m-6 3V7m6 10l4.553 2.276A1 1 0 0021 18.382V7.618a1 1 0 00-.553-.894L15 4m0 13V4m0 0L9 7" />
              </svg>
            </div>
          </div>
          <h2 className="text-2xl sm:text-3xl lg:text-4xl font-bold text-gray-900 mb-3 sm:mb-4">Trip Planner Deals</h2>
          <p className="text-base sm:text-lg text-gray-600">Plan your perfect journey with our curated travel packages.</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3 md:gap-6 max-w-4xl mx-auto mb-6 md:mb-8">
          {tripPlanners.slice(0, 4).map((tripPlanner, index) => (
            <div 
              key={tripPlanner.id}
              className="bg-white rounded-lg md:rounded-xl overflow-hidden shadow-sm md:shadow-lg transition-shadow duration-300 hover:shadow-md"
              style={{ animationDelay: `${index * 0.1}s` }}
              data-testid={`card-trip-planner-${tripPlanner.id}`}
            >
              {/* Image Section */}
              <div className="relative">
                <img 
                  src={getDestinationImage(tripPlanner.destination)} 
                  alt={tripPlanner.destination}
                  className="w-full h-32 md:h-40 object-cover"
                />
              </div>

              {/* Content Section */}
              <div className="p-3 md:p-4">
                {/* Route with arrow */}
                <div className="mb-3">
                  <h3 className="text-sm md:text-lg font-semibold text-gray-900">{tripPlanner.origin} → {tripPlanner.destination}</h3>
                  <p className="text-gray-600 text-xs md:text-sm">{tripPlanner.duration || '2h 30m'}</p>
                </div>

                {/* Flight Details */}
                <div className="space-y-2 mb-3">
                  <div className="flex items-center justify-between text-xs md:text-sm">
                    <span className="text-gray-500">Departure</span>
                    <span className="font-medium">{tripPlanner.departureDate}</span>
                  </div>
                  <div className="flex items-center justify-between text-xs md:text-sm">
                    <span className="text-gray-500">Flight</span>
                    <span className="text-blue-600 font-medium">{tripPlanner.stops || 'Non-stop'}</span>
                  </div>
                </div>

                {/* Pricing and Button */}
                <div className="flex items-center justify-between">
                  <div>
                    <div className="text-lg md:text-xl font-bold text-primary">৳{tripPlanner.price}</div>
                    <div className="text-xs text-gray-500">per person</div>
                  </div>
                  <Button 
                    onClick={handleBookNow}
                    className="bg-primary text-white px-3 py-1.5 md:px-4 md:py-2 rounded-md text-xs md:text-sm font-medium transition-colors hover:bg-primary/90"
                    data-testid={`button-book-trip-planner-${tripPlanner.id}`}
                  >
                    Select Flight
                  </Button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Navigation Arrows */}
        <div className="flex justify-center space-x-4">
          <button className="p-2 rounded-full border border-gray-300 hover:bg-gray-50 transition-colors">
            <ChevronLeft className="w-5 h-5 text-gray-600" />
          </button>
          <button className="p-2 rounded-full border border-gray-300 hover:bg-gray-50 transition-colors">
            <ChevronRight className="w-5 h-5 text-gray-600" />
          </button>
        </div>
      </div>
    </section>
  );
}

client/src/components/booking-dialog.tsx.content:
import { useState, useEffect } from 'react';
import { useMutation, useQueryClient, useQuery } from '@tanstack/react-query';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Calendar, Clock, Users, MapPin, CreditCard, User, Mail, Phone, Tag } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/useAuth';
import { apiRequest } from '@/lib/queryClient';
import type { TripPlanner, Bus, PrivateCar, Booking } from '@shared/schema';

const bookingSchema = z.object({
  customerName: z.string().min(2, 'Name must be at least 2 characters'),
  email: z.string().email('Please enter a valid email address'),
  phone: z.string().min(10, 'Phone number must be at least 10 digits'),
  passengers: z.string().min(1, 'Number of passengers is required'),
  couponCode: z.string().optional(),
  specialRequests: z.string().optional(),
  travelDate: z.string().min(1, 'Travel date is required'),
});

type BookingFormData = z.infer<typeof bookingSchema>;

interface BookingDialogProps {
  isOpen: boolean;
  onClose: () => void;
  item: TripPlanner | Bus | PrivateCar | null;
  type: 'flight' | 'bus' | 'car';
}

export default function BookingDialog({ isOpen, onClose, item, type }: BookingDialogProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedSeats, setSelectedSeats] = useState<string[]>([]);
  const [couponDiscount, setCouponDiscount] = useState(0);
  const [couponApplied, setCouponApplied] = useState(false);
  const { toast } = useToast();
  const { user, isAuthenticated } = useAuth();
  const queryClient = useQueryClient();

  const form = useForm<BookingFormData>({
    resolver: zodResolver(bookingSchema),
    defaultValues: {
      customerName: user?.firstName ? `${user.firstName} ${user.lastName || ''}`.trim() : '',
      email: user?.email || '',
      phone: '',
      passengers: '1',
      couponCode: '',
      specialRequests: '',
      travelDate: '',
    },
  });

  // Fetch booked seats for this bus and travel date
  const { data: bookedSeats = [] } = useQuery<string[]>({
    queryKey: ['/api/bookings/seats', item?.id, form.watch('travelDate')],
    enabled: type === 'bus' && !!item?.id && !!form.watch('travelDate'),
    queryFn: async () => {
      const response = await fetch(`/api/bookings/seats?busId=${item?.id}&travelDate=${form.watch('travelDate')}`);
      if (!response.ok) return [];
      const data = await response.json();
      return data.bookedSeats || [];
    },
  });

  // Reset selected seats when dialog opens or item changes
  useEffect(() => {
    if (isOpen) {
      setSelectedSeats([]);
      setCouponDiscount(0);
      setCouponApplied(false);
    }
  }, [isOpen, item?.id]);

  // Generate seat layout for bus
  const generateSeatLayout = (bus: Bus) => {
    const totalSeats = bus.seats;
    const seats = [];
    for (let i = 1; i <= totalSeats; i++) {
      seats.push(`${Math.ceil(i / 4)}${String.fromCharCode(64 + ((i - 1) % 4) + 1)}`);
    }
    return seats;
  };

  const handleSeatClick = (seatNumber: string) => {
    if (bookedSeats.includes(seatNumber)) return; // Can't select booked seats

    setSelectedSeats(prev => {
      const maxSeats = parseInt(form.watch('passengers')) || 1;
      if (prev.includes(seatNumber)) {
        return prev.filter(seat => seat !== seatNumber);
      } else if (prev.length < maxSeats) {
        return [...prev, seatNumber];
      }
      return prev;
    });
  };

  const applyCoupon = () => {
    const couponCode = form.watch('couponCode')?.toUpperCase();
    if (!couponCode) return;

    // Define coupon codes and their discounts
    const coupons: { [key: string]: number } = {
      'FLIGHT40': 40,
      'ROUND35': 35,
      'BIZCLASS30': 30,
      'BUS25SAVE': 25,
      'COMFORT20': 20,
      'LUXURY30': 30,
      'CAR40OFF': 40,
      'MICRO35': 35,
      'LUXURY50': 50,
    };

    const discount = coupons[couponCode];
    if (discount) {
      setCouponDiscount(discount);
      setCouponApplied(true);
      toast({
        title: 'Coupon Applied!',
        description: `You saved ${discount}% with code ${couponCode}`,
      });
    } else {
      setCouponDiscount(0);
      setCouponApplied(false);
      toast({
        title: 'Invalid Coupon',
        description: 'The coupon code you entered is not valid.',
        variant: 'destructive',
      });
    }
  };

  const removeCoupon = () => {
    setCouponDiscount(0);
    setCouponApplied(false);
    form.setValue('couponCode', '');
    toast({
      title: 'Coupon Removed',
      description: 'The coupon has been removed from your booking.',
    });
  };

  const bookingMutation = useMutation({
    mutationFn: async (data: BookingFormData) => {
      const bookingData = {
        itemId: item?.id,
        itemType: type,
        customerName: data.customerName,
        email: data.email,
        phone: data.phone,
        passengers: parseInt(data.passengers),
        couponCode: data.couponCode || '',
        specialRequests: data.specialRequests || '',
        travelDate: data.travelDate,
        totalAmount: Math.round((item?.price || 0) * (1 - couponDiscount / 100)),
        status: 'confirmed',
        confirmationNumber: `BDE${Math.random().toString(36).substr(2, 8).toUpperCase()}`,
        propertyName: type === 'bus' ? (item as Bus)?.operator : (type === 'flight' ? (item as TripPlanner)?.origin + ' → ' + (item as TripPlanner)?.destination : (item as PrivateCar)?.type) || '',
        propertyLocation: type === 'bus' ? (item as Bus)?.operator + ' Service' : (type === 'flight' ? (item as TripPlanner)?.origin + ' to ' + (item as TripPlanner)?.destination : 'Private Car Service'),
        propertyPhone: type === 'bus' ? '+880-1800-BUS' : (type === 'flight' ? '+880-1800-FLIGHT' : '+880-1800-CAR'),
        bookingType: type,
        ...(type === 'bus' && { selectedSeats }),
      };

      const response = await fetch('/api/bookings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(bookingData),
      });

      if (!response.ok) {
        throw new Error('Failed to create booking');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: 'Booking Confirmed!',
        description: 'Your transportation has been successfully booked.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/bookings'] });
      form.reset();
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: 'Booking Failed',
        description: error.message || 'Something went wrong. Please try again.',
        variant: 'destructive',
      });
    },
  });

  const onSubmit = async (data: BookingFormData) => {
    // Validate seat selection for buses
    if (type === 'bus' && selectedSeats.length !== parseInt(data.passengers)) {
      toast({
        title: 'Seat Selection Required',
        description: `Please select ${data.passengers} seat(s) for your booking.`,
        variant: 'destructive',
      });
      return;
    }

    setIsSubmitting(true);
    try {
      await bookingMutation.mutateAsync(data);
    } finally {
      setIsSubmitting(false);
    }
  };



  const getItemDetails = () => {
    if (!item) return [];

    switch (type) {
      case 'flight':
        const flight = item as TripPlanner;
        return [
          { label: 'Duration', value: flight.duration, icon: Clock },
          { label: 'Stops', value: flight.stops, icon: MapPin },
          { label: 'Deal Type', value: flight.dealType, icon: Badge },
        ];
      case 'bus':
        const bus = item as Bus;
        return [
          { label: 'Departure', value: bus.departure, icon: Clock },
          { label: 'Arrival', value: bus.arrival, icon: Clock },
          { label: 'Duration', value: bus.duration, icon: Clock },
          { label: 'Available Seats', value: `${bus.seats} seats`, icon: Users },
        ];
      case 'car':
        const car = item as PrivateCar;
        return [
          { label: 'Capacity', value: `${car.capacity} passengers`, icon: Users },
          { label: 'Duration', value: car.duration, icon: Clock },
          { label: 'Driver', value: car.driver, icon: User },
        ];
      default:
        return [];
    }
  };

  const getAmenities = () => {
    if (!item) return [];

    switch (type) {
      case 'bus':
        const bus = item as Bus;
        return bus.amenities || [];
      case 'car':
        const car = item as PrivateCar;
        return car.features || [];
      default:
        return [];
    }
  };

  if (!item) return null;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl font-bold">
            Complete Your Booking
          </DialogTitle>
          <DialogDescription>
            Please provide your details to confirm your {type} booking.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Booking Summary */}
          <div className="bg-gray-50 rounded-lg p-4">
            <h3 className="font-semibold text-lg mb-3">
              {type === 'bus' ? `${(item as Bus)?.operator} - ${(item as Bus)?.type}` : 
               type === 'flight' ? `${(item as TripPlanner)?.origin} → ${(item as TripPlanner)?.destination}` :
               `${(item as PrivateCar)?.type} (${(item as PrivateCar)?.category})`}
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              {getItemDetails().map((detail, index) => {
                const IconComponent = detail.icon;
                return (
                  <div key={index} className="flex items-center space-x-2">
                    <IconComponent className="w-4 h-4 text-gray-500" />
                    <span className="text-sm font-medium">{detail.label}:</span>
                    <span className="text-sm">{detail.value}</span>
                  </div>
                );
              })}
            </div>

            {getAmenities().length > 0 && (
              <div className="mb-4">
                <p className="text-sm font-medium mb-2">
                  {type === 'car' ? 'Features' : 'Amenities'}:
                </p>
                <div className="flex flex-wrap gap-2">
                  {getAmenities().map((amenity) => (
                    <Badge key={amenity} variant="outline" className="text-xs">
                      {amenity}
                    </Badge>
                  ))}
                </div>
              </div>
            )}

            <Separator className="my-4" />

            <div className="space-y-2">
              <div className="flex justify-between items-center">
                <span className="text-sm">Original Price:</span>
                <span className={`text-sm ${couponApplied ? 'line-through text-gray-500' : 'font-semibold'}`}>
                  ৳{(item?.price || 0).toLocaleString()}
                </span>
              </div>

              {couponApplied && (
                <div className="flex justify-between items-center">
                  <span className="text-sm text-green-600">Discount ({couponDiscount}%):</span>
                  <span className="text-sm text-green-600">
                    -৳{Math.round((item?.price || 0) * (couponDiscount / 100)).toLocaleString()}
                  </span>
                </div>
              )}

              <div className="flex justify-between items-center border-t pt-2">
                <span className="text-lg font-semibold">Total Amount:</span>
                <span className="text-2xl font-bold text-green-600">
                  ৳{Math.round((item?.price || 0) * (1 - couponDiscount / 100)).toLocaleString()}
                </span>
              </div>
            </div>
          </div>

          {/* Booking Form */}
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="customerName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center space-x-2">
                        <User className="w-4 h-4" />
                        <span>Full Name</span>
                      </FormLabel>
                      <FormControl>
                        <Input placeholder="Enter your full name" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center space-x-2">
                        <Mail className="w-4 h-4" />
                        <span>Email Address</span>
                      </FormLabel>
                      <FormControl>
                        <Input type="email" placeholder="your.email@example.com" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="phone"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center space-x-2">
                        <Phone className="w-4 h-4" />
                        <span>Phone Number</span>
                      </FormLabel>
                      <FormControl>
                        <Input placeholder="+880 1XX XXX XXXX" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="passengers"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center space-x-2">
                        <Users className="w-4 h-4" />
                        <span>Number of Passengers</span>
                      </FormLabel>
                      <FormControl>
                        <Input type="number" min="1" max="10" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="travelDate"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="flex items-center space-x-2">
                      <Calendar className="w-4 h-4" />
                      <span>Travel Date</span>
                    </FormLabel>
                    <FormControl>
                      <Input type="date" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Seat Selection for Bus Bookings */}
              {type === 'bus' && item && form.watch('travelDate') && (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <h3 className="text-lg font-semibold">Select Your Seats</h3>
                    <Badge variant="outline">
                      {selectedSeats.length} of {form.watch('passengers')} selected
                    </Badge>
                  </div>

                  <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-lg">
                    <div className="flex justify-center gap-6 mb-4 text-sm">
                      <div className="flex items-center gap-2">
                        <div className="w-4 h-4 bg-white dark:bg-gray-700 border-2 border-gray-300 dark:border-gray-600 rounded"></div>
                        <span>Available</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <div className="w-4 h-4 bg-green-500 border-2 border-green-600 rounded"></div>
                        <span>Selected</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <div className="w-4 h-4 bg-red-500 border-2 border-red-600 rounded"></div>
                        <span>Booked</span>
                      </div>
                    </div>

                    <div className="flex justify-center mb-4">
                      <div className="bg-gray-300 dark:bg-gray-600 px-4 py-2 rounded text-sm font-medium">
                        Driver
                      </div>
                    </div>

                    <div className="grid grid-cols-4 gap-2 max-w-md mx-auto">
                      {generateSeatLayout(item as Bus).map((seatNumber) => {
                        const isBooked = bookedSeats.includes(seatNumber);
                        const isSelected = selectedSeats.includes(seatNumber);

                        return (
                          <button
                            key={seatNumber}
                            type="button"
                            onClick={() => handleSeatClick(seatNumber)}
                            disabled={isBooked}
                            data-testid={`seat-${seatNumber}`}
                            className={`
                              aspect-square rounded-lg border-2 text-sm font-medium transition-all
                              ${isBooked 
                                ? 'bg-red-500 border-red-600 text-white cursor-not-allowed opacity-75' 
                                : isSelected
                                ? 'bg-green-500 border-green-600 text-white shadow-lg'
                                : 'bg-white dark:bg-gray-700 border-gray-300 dark:border-gray-600 hover:border-green-400 hover:bg-green-50 dark:hover:bg-green-900/20'
                              }
                            `}
                          >
                            {seatNumber}
                          </button>
                        );
                      })}
                    </div>
                  </div>
                </div>
              )}

              {/* Coupon Code Section */}
              <div className="space-y-4">
                <FormField
                  control={form.control}
                  name="couponCode"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex items-center space-x-2">
                        <Tag className="w-4 h-4" />
                        <span>Coupon Code (Optional)</span>
                      </FormLabel>
                      <div className="flex space-x-2">
                        <FormControl>
                          <Input
                            placeholder="Enter coupon code (e.g., FLIGHT40)"
                            {...field}
                            onChange={(e) => {
                              field.onChange(e);
                              if (couponApplied) {
                                setCouponApplied(false);
                                setCouponDiscount(0);
                              }
                            }}
                            className={couponApplied ? 'border-green-500' : ''}
                          />
                        </FormControl>
                        {!couponApplied ? (
                          <Button
                            type="button"
                            variant="outline"
                            onClick={applyCoupon}
                            disabled={!field.value}
                          >
                            Apply
                          </Button>
                        ) : (
                          <Button
                            type="button"
                            variant="outline"
                            onClick={removeCoupon}
                            className="text-red-600 border-red-200 hover:bg-red-50"
                          >
                            Remove
                          </Button>
                        )}
                      </div>
                      {couponApplied && (
                        <p className="text-sm text-green-600 flex items-center space-x-1">
                          <Tag className="w-3 h-3" />
                          <span>Coupon applied successfully! You saved {couponDiscount}%</span>
                        </p>
                      )}
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="specialRequests"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Special Requests (Optional)</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Any special requirements or requests..."
                        className="min-h-[80px]"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="flex space-x-4 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={onClose}
                  className="flex-1"
                  disabled={isSubmitting}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  className="flex-1 bg-green-600 hover:bg-green-700"
                  disabled={isSubmitting}
                >
                  <CreditCard className="w-4 h-4 mr-2" />
                  {isSubmitting ? 'Processing...' : 'Confirm Booking'}
                </Button>
              </div>
            </form>
          </Form>
        </div>
      </DialogContent>
    </Dialog>
  );
}

